snippets.snippet:
  $vsprecisionf

  uniform mat4 proj;
  uniform mat4 view;
  uniform mat4 model;

  attribute vec3 $position;
#ifdef VERTEXCOLORS
  attribute vec4 $color;

  varying vec4 vColor;
#endif

  void main(void) {
#ifdef VERTEXCOLORS
    vColor = $color * 0.003921568627451; // = 1/255;
#endif
    gl_Position = proj * view * vec4((model * vec4($position, 1.0)).xyz, 1.0);
  }

  $fsprecisionf

  uniform vec4 eColor;
#ifdef VERTEXCOLORS
  varying vec4 vColor;
#endif

  void main() {
#ifdef VERTEXCOLORS
    gl_FragColor = vColor * eColor;
#else
    gl_FragColor = eColor;
#endif
  }
